          margin-bottom: 0.75rem;
        }

        .med-details .detail {
          display: flex;
          align-items: center;
          gap: 0.375rem;
          font-size: 0.85rem;
          color: #64748b;
        }

        .med-instructions {
          background: #f0f9ff;
          border-radius: 8px;
          padding: 0.75rem 1rem;
          margin-bottom: 1rem;
        }

        .med-instructions .label {
          font-size: 0.75rem;
          color: #64748b;
          display: block;
          margin-bottom: 0.25rem;
        }

        .med-instructions p {
          font-size: 0.85rem;
          color: #1e293b;
          margin: 0;
        }

        .med-actions {
          display: flex;
          gap: 0.5rem;
        }

        .action-btn {
          display: flex;
          align-items: center;
          gap: 0.375rem;
          padding: 0.625rem 1rem;
          border-radius: 8px;
          font-size: 0.85rem;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.2s;
          border: none;
        }

        .action-btn.primary {
          background: #22c55e;
          color: white;
        }

        .action-btn.primary:hover { background: #16a34a; }

        .action-btn.secondary {
          background: white;
          border: 1px solid #e2e8f0;
          color: #64748b;
        }

        .action-btn.secondary:hover {
          border-color: #94a3b8;
        }

        .action-btn.danger {
          background: white;
          border: 1px solid #fecaca;
          color: #ef4444;
        }

        .action-btn.danger:hover {
          background: #fef2f2;
        }

        .completed-list {
          padding: 1rem;
        }

        .completed-item {
          display: flex;
          align-items: center;
          gap: 0.75rem;
          padding: 0.75rem;
          border-radius: 8px;
          transition: background 0.2s;
        }

        .completed-item:hover {
          background: #f8fafc;
        }

        .completed-icon {
          color: #22c55e;
          font-size: 1.25rem;
        }

        .completed-info {
          flex: 1;
        }

        .completed-info .med-name {
          display: block;
          font-weight: 500;
          color: #1e293b;
          font-size: 0.9rem;
        }

        .completed-info .med-room {
          font-size: 0.8rem;
          color: #64748b;
        }

        .given-badge {
          background: #dcfce7;
          color: #16a34a;
          padding: 0.25rem 0.75rem;
          border-radius: 8px;
          font-size: 0.8rem;
          font-weight: 500;
        }

        .schedule-table {
          overflow-x: auto;
        }

        .schedule-table table {
          width: 100%;
          border-collapse: collapse;
        }

        .schedule-table th,
        .schedule-table td {
          padding: 0.875rem 1rem;
          text-align: left;
          border-bottom: 1px solid #e2e8f0;
        }

        .schedule-table th {
          background: #f8fafc;
          font-weight: 600;
          font-size: 0.85rem;
          color: #64748b;
        }

        .schedule-table td {
          font-size: 0.9rem;
          color: #1e293b;
        }

        .status-badge {
          padding: 0.25rem 0.5rem;
          border-radius: 6px;
          font-size: 0.8rem;
          font-weight: 500;
        }

        .status-badge.pending {
          background: #fef3c7;
          color: #d97706;
        }

        .status-badge.given {
          background: #dcfce7;
          color: #16a34a;
        }

        @media (max-width: 1200px) {
          .stats-row { grid-template-columns: repeat(2, 1fr); }
        }

        @media (max-width: 768px) {
          .stats-row { grid-template-columns: 1fr; }
          .med-actions { flex-wrap: wrap; }
          .med-details { flex-direction: column; gap: 0.5rem; }
        }
      `}</style>
    </Layout>
  );
};

export default NurseMedications;
