        }

        .notif-icon.critical { color: #ef4444; }
        .notif-icon.success { color: #22c55e; }
        .notif-icon.info { color: #3b82f6; }

        .notif-content {
          flex: 1;
        }

        .notif-message {
          font-size: 0.85rem;
          color: #1e293b;
          margin-bottom: 0.25rem;
        }

