    time: med.time || 'Scheduled',
    patient: med.patient || 'Unknown',
    room: (med.room || 'N/A').replace('Room ', ''),
    medication: med.name || 'Unknown',
    dosage: med.dosage || 'N/A',
    status: med.status || 'pending'
