          <h2>Medication Schedule Overview</h2>
        </div>
        <div className="schedule-table">
          <table>
            <thead>
              <tr>
                <th>Time</th>
                <th>Patient</th>
                <th>Room</th>
                <th>Medication</th>
                <th>Dosage</th>
                <th>Status</th>
