                  <td>{item.patient}</td>
                  <td>{item.room}</td>
                  <td>{item.medication}</td>
                  <td>{item.dosage}</td>
                  <td>
                    <span className={`status-badge ${item.status}`}>
                      {item.status === 'given' ? 'Given' : 'Pending'}
