import { useState, useEffect, useRef } from 'react';
import Layout from '../../components/common/Layout';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const NurseMessages = () => {
  const { user } = useAuth();
  const [contacts, setContacts] = useState([]);
  const [selectedContact, setSelectedContact] = useState(null);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [alerts, setAlerts] = useState([]);
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [emergencyType, setEmergencyType] = useState('');
  const [emergencyLocation, setEmergencyLocation] = useState('');
  const [emergencyDetails, setEmergencyDetails] = useState('');
  const [loadingContacts, setLoadingContacts] = useState(true);
  const [loadingMessages, setLoadingMessages] = useState(false);
  const [sendingMessage, setSendingMessage] = useState(false);
  const messagesEndRef = useRef(null);
  const pollIntervalRef = useRef(null);
  const contactsPollRef = useRef(null);


  useEffect(() => {
    fetchContacts();
    fetchAlerts();

    // Poll contacts every 10s to reflect presence changes
    if (contactsPollRef.current) clearInterval(contactsPollRef.current);
    contactsPollRef.current = setInterval(() => {
      fetchContacts();
    }, 10000);

    // Cleanup polling on unmount
    return () => {
      if (pollIntervalRef.current) clearInterval(pollIntervalRef.current);
      if (contactsPollRef.current) clearInterval(contactsPollRef.current);
    };
  }, []);

  useEffect(() => {
    if (selectedContact) {
      fetchChatWithUser(selectedContact._id);

      // Poll for new messages every 5 seconds
      if (pollIntervalRef.current) {
        clearInterval(pollIntervalRef.current);
      }
      pollIntervalRef.current = setInterval(() => {
        if (currentChatId) {
          fetchMessagesForChat(currentChatId, false);
        }
      }, 5000);
    }

    return () => {
      if (pollIntervalRef.current) {
        clearInterval(pollIntervalRef.current);
      }
    };
  }, [selectedContact]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const fetchContacts = async () => {
    try {
      setLoadingContacts(true);
      const resp = await api.get('/chat/contacts');
      const data = resp.data;
      // Transform contacts data
      const transformedContacts = (data.data || []).filter(c => c._id !== user?._id).map(contact => ({
        ...contact,
        id: contact._id,
        name: contact.fullName,
        avatar: contact.fullName?.charAt(0) || '?',
        role: contact.role?.charAt(0).toUpperCase() + contact.role?.slice(1) || 'Staff',
        status: contact.isLoggedIn ? 'online' : 'offline',
        category: contact.role === 'doctor' ? 'doctors' : contact.role === 'nurse' ? 'nurses' : 'departments',
        unread: 0
      }));
      setContacts(transformedContacts);
    } catch (error) {
      console.error('Failed to fetch contacts:', error);
      setContacts([]);
    } finally {
      setLoadingContacts(false);
    }
  };

  const fetchChatWithUser = async (userId) => {
    try {
      setLoadingMessages(true);
      const resp = await api.get(`/chat/with/${userId}`);
      const chat = resp.data?.data || resp.data;
      setCurrentChatId(chat._id);

      // Transform messages
      const transformedMessages = (chat.messages || []).map(msg => ({
        id: msg._id,
        senderId: msg.senderId,
        text: msg.content,
        time: new Date(msg.timestamp).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
        isOwn: msg.senderId === user?._id || msg.senderId?._id === user?._id,
        read: msg.read
      }));
      setMessages(transformedMessages);

      // Mark messages as read
      if (chat._id) {
        api.patch(`/chat/${chat._id}/read`).catch(err => console.error('Failed to mark as read:', err));
      }
    } catch (error) {
      console.error('Failed to fetch chat:', error);
      setMessages([]);
    } finally {
      setLoadingMessages(false);
    }
  };

  const fetchMessagesForChat = async (chatId, showLoading = true) => {
    try {
      if (showLoading) setLoadingMessages(true);
      const resp = await api.get(`/chat/${chatId}/messages`);
      const data = resp.data;
      const transformedMessages = (data.data || data || []).map(msg => ({
        id: msg._id,
        senderId: msg.senderId,
        text: msg.content,
        time: new Date(msg.timestamp).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
        isOwn: msg.senderId === user?._id || msg.senderId?._id === user?._id,
        read: msg.read
      }));
      setMessages(transformedMessages);
    } catch (error) {
      console.error('Failed to fetch messages:', error);
    } finally {
      if (showLoading) setLoadingMessages(false);
    }
  };

  const fetchAlerts = async () => {
    // Alerts are not persisted in our current implementation
    setAlerts([]);
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !selectedContact || sendingMessage) return;

    const messageText = newMessage;
    setNewMessage('');

    // Optimistic update
    const optimisticMessage = {
      id: Date.now(),
      senderId: user?._id,
      text: messageText,
      time: new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
      isOwn: true,
    };
    setMessages(prev => [...prev, optimisticMessage]);

    try {
      setSendingMessage(true);
      const resp = await api.post(`/chat/send/${selectedContact._id}`, { content: messageText });
      const data = resp.data;

      // Update chat ID if we didn't have one
      if (!currentChatId && (data.data?.chatId || data.chatId)) {
        setCurrentChatId(data.data?.chatId || data.chatId);
      }

      // Refresh messages to get server-confirmed message
      const newChatId = currentChatId || data.data?.chatId || data.chatId;
      if (newChatId) {
        await fetchMessagesForChat(newChatId, false);
      }
    } catch (error) {
      console.error('Failed to send message:', error);
      // Remove optimistic message on failure
      setMessages(prev => prev.filter(msg => msg.id !== optimisticMessage.id));
      setNewMessage(messageText); // Restore the message
      alert('Failed to send message. Please try again.');
    } finally {
      setSendingMessage(false);
    }
  };

