        .pill.critical.active { background: #ef4444; border-color: #ef4444; }
        .pill.moderate.active { background: #f59e0b; border-color: #f59e0b; }
        .pill.stable.active { background: #22c55e; border-color: #22c55e; }

        .results-count {
