        _id: '3', name: 'Insulin', dosage: '10 units', route: 'Subcutaneous',
        patient: 'Patient 3', room: '201C', time: '11:00 AM',
        priority: 'medium', status: 'pending',
        instructions: 'Check blood sugar before administration.'
