        _id: '4', name: 'Morphine', dosage: '5mg', route: 'IV',
        patient: 'Patient 4', room: '308D', time: '11:30 AM',
        priority: 'high', status: 'pending',
        instructions: 'Monitor respiratory rate and pain level.'
